#get price data for specified ticker
data = pdr.get_data_yahoo(tickers, start = startDate, end = endDate)
prices = data["Adj Close"].dropna(axis = 'columns')
prices.sort_index(ascending = False, inplace = True)

keysList = prices.keys()
volList = []

#calcuate specific values such as annual volatility and annual return for benchmark such as spy
benchmark = pdr.get_data_yahoo('SPY', start = startDate, end = endDate)
benchmarkPrices = benchmark["Adj Close"]
benchmarkPrices.sort_index(ascending = False, inplace = True)
benchVol = (benchmarkPrices - benchmarkPrices.shift(-1)) / benchmarkPrices.shift(-1)
benchDailyVol = np.std(benchVol)
benchAnnualVol = (benchDailyVol * math.sqrt(len(benchVol))) * 100
benchAnnualReturn = ((benchmarkPrices[0] / benchmarkPrices[-1]) - 1) * 100
benchRatio = benchAnnualReturn / benchAnnualVol
volList.append(['SPY', benchAnnualVol, benchAnnualReturn, benchRatio, 0])

#calcuate specific values such as annual volatility and annual return
for key in keysList:
	data = pdr.get_quote_yahoo(key)

	if('trailingPE' in data.columns and 'forwardPE' in data.columns):
		pastPE = int(data['trailingPE'].item())
		futurePE = int(data['forwardPE'].item())

		if(pastPE < futurePE and pastPE > 10):
			vol = (prices[key] - prices[key].shift(-1)) / prices[key].shift(-1)
			dailyVol = np.std(vol)
			annualVol = (dailyVol * math.sqrt(len(vol))) * 100

			if(annualVol < 75):
				annualReturn = ((prices[key][0] / prices[key][-1]) - 1) * 100
				ratio = annualReturn / annualVol

				if(ratio > benchRatio):
					volList.append([key, annualVol, annualReturn, ratio, pastPE])

#sort list by ratio
sortedList = sorted(volList, key = itemgetter(3), reverse=True)

#display results in order from best performing to worst performing securities by annual return adjusted by volatility
for item in sortedList:
	print(str(item[0]) + " has a return of: " + str(round(item[2], 2)) + "% with PE: " + str(item[4]))
	print("When adjusted for volatility: " + str(round(item[1], 2)) + "%, the ratio is : " + str(round(item[3], 2)), end = '\n\n')